import React, { useState, useEffect } from 'react'


const Attributes = (props) => {

    const { attr_list, attributes, setAttributes, skills_list } = props
    const [abilityModifiers, setAbilityModifiers] = useState({});
    const [skills, setSkills] = useState({})
    const [totalPoints, setTotalPoints] = useState()

    useEffect(() => {
        const newAbilityModifiers = {};
        for (const attribute in attributes) {
            const value = attributes[attribute];
            const modifier = Math.max(Math.floor((value - 10) / 2), 0);
            newAbilityModifiers[attribute] = modifier;
        }
        setAbilityModifiers(newAbilityModifiers);
        setTotalPoints(10 + (4*newAbilityModifiers["Intelligence"]))

    }, [attributes]);

    console.log(Object.entries(skills_list))



    /* const skillFunc = () => {

    }; */

    const handleIncrement = (attribute) => {
        const updatedAttributes = { ...attributes, [attribute]: attributes[attribute] + 1, };
        setAttributes(updatedAttributes);
    };

    const handleDecrement = (attribute) => {
        const updatedAttributes = { ...attributes, [attribute]: Math.max(attributes[attribute] - 1, 1), };
        setAttributes(updatedAttributes);
    };





    return (
        <>
            <h2>Attributes:</h2>
            {attr_list.map((attribute) => (
                <div key={attribute}>
                    <div>{`${attribute}: `}
                        <span>{attributes[attribute]}</span>
                        <button onClick={() => handleDecrement(attribute)}>-</button>
                        <button onClick={() => handleIncrement(attribute)}>+</button>
                        <div>Ability Modifier: {abilityModifiers[attribute]}</div>

                    </div>

                </div>
            ))}
            <h2>Total Points Available: {totalPoints}</h2>
            <h2>Skills:</h2>
            {skills_list.map((skill) => (
                <div>{skill.name}</div>
            ))}
        </>
    );
};

export default Attributes
