import React, { useState, useEffect } from 'react';

const SKILL_LIST = {
  'Acrobatics': 'Dexterity',
  'Athletics': 'Strength',
  'Deception': 'Charisma',
  'History': 'Intelligence',
  // Add more skills and corresponding attributes here
};

const Skills = ({ attributes }) => {
  const [skillPoints, setSkillPoints] = useState({});
  const [totalPoints, setTotalPoints] = useState(0);

  useEffect(() => {
    const intModifier = Math.max(Math.floor((attributes.Intelligence - 10) / 2), 0);
    const newTotalPoints = 10 + (4 * intModifier);
    setTotalPoints(newTotalPoints);

    const newSkillPoints = {};
    for (const skill in SKILL_LIST) {
      const attribute = SKILL_LIST[skill];
      const modifier = Math.max(Math.floor((attributes[attribute] - 10) / 2), 0);
      newSkillPoints[skill] = { points: 0, modifier, total: modifier };
    }
    setSkillPoints(newSkillPoints);
  }, [attributes]);

  const handleSkillChange = (skill, value) => {
    const skillPointsCopy = { ...skillPoints };
    const skillTotalPoints = skillPointsCopy[skill].points + value;
    if (skillTotalPoints < 0 || skillTotalPoints > totalPoints) {
      return;
    }
    skillPointsCopy[skill].points = skillTotalPoints;
    skillPointsCopy[skill].total = skillTotalPoints + skillPointsCopy[skill].modifier;
    setSkillPoints(skillPointsCopy);
  };

  return (
    <>
      <h2>Skills:</h2>
      <div>Total points available: {totalPoints}</div>
      {Object.keys(skillPoints).map((skill, index) => {
        return (
          <div key={index}>
            {skill} - points: {skillPoints[skill].points}
            <button onClick={() => handleSkillChange(skill, 1)}>+</button>
            <button onClick={() => handleSkillChange(skill, -1)}>-</button>
            modifier: {skillPoints[skill].modifier} total: {skillPoints[skill].total}
          </div>
        );
      })}
    </>
  );
};

export default Skills;
