import { useState, useEffect } from 'react';
import './App.css';
import { ATTRIBUTE_LIST, CLASS_LIST, SKILL_LIST } from './consts.js';
import Attributes from './components/Attributes/Attributes';
import Classes from './components/Classes/Classes';
import Skills from './components/Skills/Skills';


function App() {

  const initialAttributes = {};
  ATTRIBUTE_LIST.forEach(attribute => {
    initialAttributes[attribute] = 10;
  });

  const [attributes, setAttributes] = useState(initialAttributes);

  const [abilityModifiers, setAbilityModifiers] = useState({});

  const initSkillPoints = {}
  SKILL_LIST.forEach((skill) => {
    initSkillPoints[skill.name] = 0
  })

  const [skillPoints, setSkillPoints] = useState(initSkillPoints)

  const [totalPoints, setTotalPoints] = useState(0)

  useEffect(() => {
    const intModifier = modifierCalc(attributes.Intelligence)
    const totPoints = 10 + (4 * intModifier)
    setTotalPoints(totPoints)
  }, [attributes.Intelligence])

  const modifierCalc = (attribute) => {
    let attModifier = 0
    if (attribute > 9) {
      attModifier = Math.floor((attribute - 10) / 2)
    }
    return attModifier

  }


  // console.log(JSON.stringify(attributes))

  const saveCharacter = async (data) => {

    const [attribs, abilityMods] = data
    console.log(data)

    try {
      fetch('https://recruiting.verylongdomaintotestwith.ca/api/MatthewOlaka/character',
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(data)

        });

      alert("Player saved successfully")

    } catch (error) {
      console.error(error);
    }
  }

  const downloadCharacter = async () => {

    try {
      fetch('https://recruiting.verylongdomaintotestwith.ca/api/MatthewOlaka/character')
        .then(resp => resp.json())
        .then(data => {
          console.log(data.body)
        }
        )
    } catch (err) {
      console.log(err)
    }
  }



  useEffect(() => {
    const newAbilityModifiers = {};
    for (const attribute in attributes) {
      const value = attributes[attribute];
      const modifier = Math.max(Math.floor((value - 10) / 2), 0);
      newAbilityModifiers[attribute] = modifier;
    }
    setAbilityModifiers(newAbilityModifiers);



  }, [attributes]);

  const handleInc = (skill) => {
    if (totalPoints > 0) {
      const updatedSkillPoints = { ...skillPoints }
      updatedSkillPoints[skill] += 1
      setSkillPoints(updatedSkillPoints)

      setTotalPoints(totalPoints - 1)
    }

    // const updatedSkillPoints = { ...skillPoints, [skill]: skillPoints[skill] + 1, };
    // setSkillPoints(updatedSkillPoints)
  }
  const handleDec = (skill) => {

    const updatedSkillPoints = { ...skillPoints }
    updatedSkillPoints[skill] -= 1
    setSkillPoints(updatedSkillPoints)
    setTotalPoints(totalPoints + 1)

  }



  return (
    <div className="App">
      <header className="App-header">
        <h1>React Coding Exercise</h1>
      </header>
      <section className="App-section">
        <button onClick={() => saveCharacter([attributes, abilityModifiers,])}>Save Character</button>
        &nbsp;
        <button onClick={() => downloadCharacter(attributes)}>Download Character</button>
        <Classes att_list={ATTRIBUTE_LIST} CLASS_LIST={CLASS_LIST} attributes={attributes} />
        <Attributes attr_list={ATTRIBUTE_LIST} attributes={attributes} setAttributes={setAttributes} abilityModifiers={abilityModifiers} />
        <Skills attributes={attributes} skills={SKILL_LIST} abilityModifiers={abilityModifiers} skillPoints={skillPoints} setSkillPoints={setSkillPoints} totalPoints={totalPoints} setTotalPoints={setTotalPoints} />
        <h2>Skills:</h2>
            <h3>Total Points available: {totalPoints}</h3>
            {SKILL_LIST.map((skill) => (
                <div key={skill.name}>{skill.name} -
                    points:
                    &nbsp;
                    {skillPoints[skill.name]}
                    &nbsp;
                    <button onClick={() => handleInc(skill.name)}>+</button>
                    &nbsp;
                    { skillPoints[skill.name] > 0 ? 
                        <button onClick={() => handleDec(skill.name)}>-</button> :
                        <button>-</button> 
                    }
                    &nbsp;
                    modifier: {abilityModifiers[skill.attributeModifier]}
                    &nbsp;
                    total: {abilityModifiers[skill.attributeModifier] + skillPoints[skill.name]}
                </div>
            ))}

      </section>
    </div>
  );
}

export default App;
