import React from 'react'
import { ATTRIBUTE_LIST, CLASS_LIST } from '../../consts';

function Classes({ attributes }) {

    const meetsRequirements = (className) => {
        const classRequirements = CLASS_LIST[className];
        for (const [attribute, value] of Object.entries(classRequirements)) {
            if (attributes[attribute] < value) {
                return false;
            }
        }
        return true;
    };

    const getClassStyle = (className) => {
        return meetsRequirements(className) ? { color: 'red' } : {};
    };

    const handleClick = (className) => {
        const classRequirements = CLASS_LIST[className];
        const requiredAttributes = {};
        for (const attribute of ATTRIBUTE_LIST) {
            if (classRequirements[attribute]) {
                requiredAttributes[attribute] = classRequirements[attribute];
            } else {
                requiredAttributes[attribute] = 0;
            }
        }
    };

    return (
        <div>
            <h2>Classes:</h2>
            <div>
                {Object.keys(CLASS_LIST).map((className) => (
                    <div key={className} style={getClassStyle(className)} onClick={() => handleClick(className)}>
                        {className}
                    </div>
                ))}
            </div>
        </div>
    );
}


export default Classes
