import { useState } from 'react';
import './App.css';
import { ATTRIBUTE_LIST, CLASS_LIST, SKILL_LIST } from './consts.js';
import Attributes from './components/Attributes/Attributes';
import Classes from './components/Classes/Classes';
import Skills from './components/Skills/Skills';


function App() {

  // const [attributes, setAttributes] = useState({
  //   Strength: 10,
  //   Dexterity: 10,
  //   Constitution: 10,
  //   Intelligence: 10,
  //   Wisdom: 10,
  //   Charisma: 10,
  // });
  const initialAttributes = {};
  ATTRIBUTE_LIST.forEach(attribute => {
    initialAttributes[attribute] = 10;
  });

  const [attributes, setAttributes] = useState(initialAttributes);

  return (
    <div className="App">
      <header className="App-header">
        <h1>React Coding Exercise</h1>
      </header>
      <section className="App-section">
        <Attributes attr_list={ATTRIBUTE_LIST} attributes={attributes} setAttributes={setAttributes} />
        <Classes att_list={ATTRIBUTE_LIST} CLASS_LIST={CLASS_LIST} attributes={attributes} />
        <Skills SKILL_LIST={SKILL_LIST} />

      </section>
    </div>
  );
}

export default App;
